import React, { Component } from "react";
import Fade from "react-reveal/Fade";

class MobileBar extends Component {
  constructor(props) {
    super();

    this.state = {
      active: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  //Clicks
  handleClick() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  }



  render() {
    if (!this.state.active) {
      return (
        <div>
          <Fade>
            <ul className="header">
              <li><a onClick={this.handleClick}>Menu</a></li>
            </ul>
          </Fade>
        </div>
      );
    } else {
      return (
        <div>
           <Fade up>
            <ul className="header">
              <li><a>Project</a></li>
            </ul>
          </Fade>
        </div>
      );
    }
  }
}

export default MobileBar;
