import React, { Component } from "react";
import Fade from "react-reveal/Fade";

class MobileBar extends Component {
  constructor(props) {
    super();

    this.state = {
      active: false
    };
    this.handleClick = this.handleClick.bind(this);
    this.redirect = this.redirect.bind(this);
  }

  redirect() {
    this.props.history.push("/About");
  }

  //Clicks
  handleClick() {
    this.setState(state => ({
      active: !state.active
    }));
  }

  handleClick2() {
    this.setState(state => ({
      active: !state.active
    }));
  }

  render() {
    return (
      <div>
        <Fade>
          <ul className="header">
            <li>
              <a className="menubutton active" onClick={this.handleClick}>
                Menu
              </a>
            </li>
            <Fade up when={this.state.active} collapse>
              <ul className="header top2">
                <li>
                  <a className="menubutton" onClick={this.handleClick2}>
                    Project
                  </a>
                </li>
              </ul>
            </Fade>
          </ul>
        </Fade>
      </div>
    );
  }
}

export default MobileBar;
