import React, { Component } from "react";
import Fade from "react-reveal/Fade";

class MobileBar extends Component {
  constructor(props) {
    super();

    this.state = {
      active: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  //Clicks
  handleClick() {
    this.setState(state => ({
      active: !state.active
    }));
  }

  render() {
    return (
      <div>
        <Fade>
          <ul className="header">
            <li>
              <a onClick={this.handleClick}>Menu</a>
              <a Fade up when={this.state.active} collapse>
                Project
              </a>
            </li>
          </ul>
        </Fade>
      </div>
    );
  }
}

export default MobileBar;
