import React, { Component } from "react";
import Fade from "react-reveal/Fade";
import { withRouter } from "react-router-dom";

class MobileBar extends Component {
  constructor(props) {
    super();

    this.state = {
      active: false
    };
    this.handleClick = this.handleClick.bind(this);
    this.redirectProject = this.redirectProject.bind(this);
  }

  redirectHome() {
    this.props.history.push("/");
    this.setState(state => ({
      active: !state.active
    }));
  }

  redirectProject() {
    this.props.history.push("/Project");
    this.setState(state => ({
      active: !state.active
    }));
  }

  //Clicks
  handleClick() {
    this.setState(state => ({
      active: !state.active
    }));
  }

  render() {
    return (
      <div>
        <Fade>
          <ul className="header">
            <li>
              <a
                className={
                  this.state.active ? "menubutton active" : "menubutton"
                }
                onClick={this.handleClick}
              >
                Menu
              </a>
            </li>
            <Fade up when={this.state.active} collapse>
              <ul className="header top2">
                <li>
                  <a className="menubutton" onClick={this.redirectProject}>
                    Project
                  </a>
                </li>
              </ul>
            </Fade>
          </ul>
        </Fade>
      </div>
    );
  }
}

export default withRouter(MobileBar);
